INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(2, 4, '2014-04-14 09:37:24', '2014-04-14 09:37:24', '<h1>A Monthly Box of<br class="hidden-xs" /> Delicious Saké Samples</h1>\r\n<h3>Subscribe and get notified when we launch,<br class="hidden-xs" /> or shop for full-size saké now!</h3>', 'Home', '', 'publish', 'open', 'open', '', 'sample-page', '', '', '2014-04-28 14:58:08', '2014-04-28 14:58:08', '', 0, 'http://yokoloko.com/?page_id=2', 0, 'page', '', 0),
(8, 4, '2014-04-14 09:59:55', '2014-04-14 09:59:55', 'In the <a title="Japanese language" href="http://en.wikipedia.org/wiki/Japanese_language">Japanese language</a>, the word "<i>sake</i>" (酒, "liquor", also pronounced <a title="Go-on" href="http://en.wikipedia.org/wiki/Go-on"><i>shu</i></a>) generally refers to any alcoholic drink, while the beverage called "sake" in English is usually termed <i>nihonshu</i> (日本酒, "Japanese liquor"). Under Japanese liquor laws, sake is labelled with the word "seishu" (清酒, "clear liquor"), a synonym less commonly used colloquially.', 'Sake Number One', '', 'trash', 'open', 'closed', '', 'sake-number-one', '', '', '2014-04-25 09:09:57', '2014-04-25 09:09:57', '', 0, 'http://yokoloko.com/?post_type=product&#038;p=8', 0, 'product', '', 0),
(10, 4, '2014-04-14 10:04:26', '2014-04-14 10:04:26', 'Ohai', 'The Sake Bomb', '', 'publish', 'open', 'open', '', 'the-sake-bomb', '', '', '2014-04-27 00:38:23', '2014-04-27 00:38:23', '', 0, 'http://yokoloko.com/?page_id=10', 0, 'page', '', 0),
